import * as React from "react";
import firebase from "../../firebase";

const initialState: IProfile = {
    name: "Brian Inoa",
    github: "inoabrian",
    company: "UPS",
    // tslint:disable-next-line:max-line-length
    picture: "",
    social_media: [{
        uri: "https://www.linkedin.com/in/brian-inoa-bb80597b/",
        logo: "linkedIn",
        user_name: "brian-inoa-bb80597b"
    }]
};

type State = typeof initialState;

export interface ISocialMedia {
    uri: String;
    logo: String;
    user_name: String;
}

export interface IProfile {
    name: string;
    github: string;
    company: string;
    picture: string;
    social_media: Array<ISocialMedia>;
}

const storage: any = firebase.storage!();

export class Profile extends React.Component<IProfile, State> {
    state: State;
    constructor(props: any) {
        super(props);

        this.state = initialState;

        const files: Array<string> = ["brian_and_mia.jpg"];

        files.map(filename => {
            storage
                .ref(`${filename}`)
                .getDownloadURL()
                .then((image: any) => {
                    this.state = { ...this.state, picture: `${image.bucket}${image.name}` };
                });
        });
    }

    render(): JSX.Element {
        return (
            <div className="row">
                <div className="col-12 align-self-center">
                    <img
                        className="img-thumbnail mx-auto d-block"
                        src={this.state.picture}
                        style={{ height: "200px", width: "200px" }}
                        alt="Brian Inoa"></img>
                </div>
                <div>{this.state.name}</div>
                <div>{this.state.company}</div>
            </div>
        );
    }
}
